import React, { Component } from "react";
import PartTitle from "../components/PartTitle";
const SERVER_ADDRESS = process.env.REACT_APP_SERVER_ADDRESS;

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: "",
      phonenumber: "",
      email: "",
      message: "",
      fullnameError: false,
      emailError: false,
      messageError: false,
    };
  }

  updateData = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({ [name]: value });
  };

  submitForm = (event) => {
    event.preventDefault();
    const {
      fullname,
      email,
      phonenumber,
      message,
      fullnameError,
      emailError,
      messageError,
    } = this.state;

    if (fullname === "") {
      this.setState({ fullnameError: true });
    }

    fetch(SERVER_ADDRESS + "/sendmail", {
      method: "POST",
      headers: new Headers({
        "Content-Type": "application/json",
      }),
      body: JSON.stringify({
        fullname,
        email,
        phonenumber,
        message,
      }),
    }).then((res) => {
      console.log(res);
      if (res.ok) {
        // this.setState({ isSent: true }, () => {
        //   setTimeout(() => {
        //     this.setState({ isSent: false });
        //   }, 5000);
        // });
      }
    });
  };

  render() {
    return (
      <>
        <PartTitle title="Contact" />

        <form className="contact-container" onSubmit={this.submitForm}>
          <div className="contact-container__top">
            <div className="contact-container__items">
              <p className="contact-container__title">Nom *</p>
              <input
                onChange={this.updateData}
                className="contact-container__input"
                name="fullname"
              />
            </div>
            <div className="contact-container__items">
              <p className="contact-container__title">Numéro de téléphone</p>
              <input
                onChange={this.updateData}
                className="contact-container__input"
                name="phonenumber"
              />
            </div>
            <div className="contact-container__items">
              <p className="contact-container__title">Email *</p>
              <input
                onChange={this.updateData}
                className="contact-container__input"
                name="email"
              />
            </div>
          </div>
          <div className="contact-container__bottom">
            <p className="contact-container__title">Message *</p>
            <textarea
              spellcheck="false"
              onChange={this.updateData}
              className="contact-container__textarea"
              name="message"
            ></textarea>
            <div>
              <button className="contact-container__button" type="submit">
                Envoyer
              </button>
            </div>
          </div>
        </form>
      </>
    );
  }
}

export default Contact;
