import React, { Component } from "react";
import { motion } from "framer-motion";

class Error extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  render() {
    return (
      <motion.div
        initial={{ bottom: "-10%" }}
        animate={{ bottom: "-2%" }}
        transition={{ duration: 2 }}
        className="error-container"
      >
        {this.props.message}
      </motion.div>
    );
  }
}

export default Error;
