import React, { Component } from "react";
import PartTitle from "../components/PartTitle";
import Error from "./Error";

const SERVER_ADDRESS = process.env.REACT_APP_SERVER_ADDRESS;

class Contact extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullname: "",
      phonenumber: "",
      email: "",
      message: "",
      fullnameError: false,
      emailError: false,
      messageError: false,
      seeError: false,
      errorMessage: "",
    };
  }

  switchError = () => {
    this.setState({ seeError: false });
  };

  updateData = (event) => {
    const name = event.target.name;
    const value = event.target.value;
    this.setState({ [name]: value });
  };

  errorMessage = (message) => {
    this.setState({ seeError: true });
    this.setState({ errorMessage: message });
  };

  submitForm = (event) => {
    event.preventDefault();
    const {
      fullname,
      email,
      phonenumber,
      message,
      fullnameError,
      emailError,
      messageError,
    } = this.state;

    if (fullname === "") {
      this.setState({ fullnameError: true });
    } else {
      this.setState({ fullnameError: false });
    }

    if (email === "") {
      this.setState({ emailError: true });
    } else {
      this.setState({ emailError: false });
    }

    if (message === "") {
      this.setState({ messageError: true });
    } else {
      this.setState({ messageError: false });
    }

    if (fullname !== "" && email !== "" && message !== "") {
      fetch(SERVER_ADDRESS + "/sendmail", {
        method: "POST",
        headers: new Headers({
          "Content-Type": "application/json",
        }),
        body: JSON.stringify({
          fullname,
          email,
          phonenumber,
          message,
        }),
      }).then((res) => {
        console.log(res);
        if (res.ok) {
          // this.setState({ isSent: true }, () => {
          //   setTimeout(() => {
          //     this.setState({ isSent: false });
          //   }, 5000);
          // });
        }
      });
    } else {
      this.errorMessage("Veuillez remplir tous les champs!");
    }
  };

  render() {
    const {
      fullname,
      message,
      email,
      fullnameError,
      emailError,
      messageError,
    } = this.state;
    return (
      <>
        <PartTitle title="Contact" />

        <form className="contact-container" onSubmit={this.submitForm}>
          <div className="contact-container__top">
            <div className="contact-container__items">
              <p className="contact-container__title">Nom *</p>
              <input
                onChange={this.updateData}
                className={
                  fullnameError
                    ? "contact-container__input contact-container__input--red"
                    : "contact-container__input"
                }
                name="fullname"
              />
            </div>
            <div className="contact-container__items">
              <p className="contact-container__title">Numéro de téléphone</p>
              <input
                onChange={this.updateData}
                className="contact-container__input"
                name="phonenumber"
              />
            </div>
            <div className="contact-container__items">
              <p className="contact-container__title">Email *</p>
              <input
                onChange={this.updateData}
                className={
                  emailError
                    ? "contact-container__input contact-container__input--red"
                    : "contact-container__input"
                }
                name="email"
              />
            </div>
          </div>
          <div className="contact-container__bottom">
            <p className="contact-container__title">Message *</p>
            <textarea
              spellCheck="false"
              onChange={this.updateData}
              className={
                messageError
                  ? "contact-container__textarea contact-container__textarea--red"
                  : "contact-container__textarea"
              }
              name="message"
            ></textarea>
            <div>
              <button
                className={
                  fullname === "" || message === "" || email === ""
                    ? "contact-container__button contact-container__button--disable"
                    : "contact-container__button "
                }
                type="submit"
              >
                Envoyer
              </button>
            </div>
          </div>
        </form>

        {this.state.seeError && <Error message={this.state.errorMessage} />}
      </>
    );
  }
}

export default Contact;
